import React from 'react';
import $ from'jquery';
import Button from './ui/Button';
import { Flip } from 'react-reveal';
import 'jquery-ui-dist/jquery-ui';
import * as garden from './Garden';
import * as colors from './jscolor';

// import './Garden';
// import './jscolor';
// import { Component } from 'react';
$(function clock(){
  setInterval( () => {
  const fullDate = new Date();
    var hours = fullDate.getHours();
    var minutes = fullDate.getMinutes();
    if (hours<10){
      hours = "0" + hours
    }
    if (minutes < 10){
      minutes = "0" + minutes
    }
  
    document.getElementById('hour').innerHTML = hours;
    document.getElementById('minute').innerHTML = ": " + minutes;
    }, 100);
  
  }) 
  /*Relogio Digital*/
  
  
  /*Relogio analogico -- foi preciso colocar o new date numa variável certo??*/ 
$(function timerTick() {
    const aqui =new Date();
      var h = 30 * ((aqui.getHours() % 12) + aqui.getMinutes() / 60);
      var m = 6 * aqui.getMinutes();
      var s = 6 * aqui.getSeconds();
      document.getElementById('hour_pointer').setAttribute('transform', 'rotate(' + h + ', 50, 50)');
      document.getElementById('minute_pointer').setAttribute('transform', 'rotate(' + m + ', 50, 50)');
      document.getElementById('second_pointer').setAttribute('transform', 'rotate(' + s + ', 50, 50)');
      setTimeout(timerTick, 100);
      setInterval(timerTick,100);
    }) 
  /*Relogio analogico*/

const Gardens = () => {
    return (
      <div>
  <meta charSet="utf-8" />
  <title>Garden</title>
  <link rel="stylesheet" type="text/css" href="main.scss" />  
  {/* aqui */}
  <div className="big_div">
    <div id="toggle-btn" onClick={garden.togglemenu}>
      <span />
      <span />
      <span />
    </div>
    <div id="sidebar">
      <ul>
        <li className="elementos_menu_direito" onClick={garden.togglemenu1}>Watch Types</li>
        <div id="first_menu_hidden">
          <div id="dragdiv">
            <div className="child elementos " id="node1">
              <svg height={70} width={200}>
                <text x={0} y={15} />
              </svg>
              <span/>
              <span/>
              <span/>
            </div>
          </div>
        </div>
        <div id="remaining_01">
          <li className="elementos_menu_direito" onClick={garden.togglemenu2}>Colors</li>
          <div id="second_menu_hidden">
            <p>Background Colors <input className="jscolor {onFineChange:'background_color(this)'}" defaultValue={'000'} /> </p>
            <p>Digital <input className="jscolor {onFineChange:'digital_color(this)'}" defaultValue="fff" /> </p>
            <p>Analog Hours <input className="jscolor {onFineChange:'analog_hours(this)'}" defaultValue="fff" /> </p>
            <p>Analog Minuts <input className="jscolor {onFineChange:'analog_minutes(this)'}" defaultValue="fff" /> </p>
            <p>Analog Seconds <input className="jscolor {onFineChange:'analog_seconds(this)'}" defaultValue="ff0000" /> </p>
            <p>Center Dot <input className="jscolor {onFineChange:'center_circule(this)'}" defaultValue="fff" /> </p>
          </div>
        </div>
      </ul>
    </div>
    <div className="watch">
      <div className="center_circule">
        <p id="rect" className="center_circule" />
        <div id="dropdiv">
          <div className="child elementos" id="node2">
            <svg id="clock" viewBox="0 0 100 100" width={200} height={200}>
              <g id="pointers">
                <line id="hour_pointer" x1={50} y1={50} x2={50} y2={27} />
                <line id="minute_pointer" x1={50} y1={50} x2={50} y2={17} />
                <line id="second_pointer" x1={50} y1={50} x2={50} y2={13} />
                <circle id="center_dot" style={{stroke: 'white', strokeWidth: '3px'}} cx={50} cy={50} r={1} />
              </g>
            </svg>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

  ); 
}
export default Gardens;
